class SubmitExecute{
	
	private $_apiSubmitUrl	= '';	//数据提交地址
	private $_kLog			= '';	//日志对象
	
	public function __construct($url = ''){
		
		if($url == '' && OPEN_DEBUG) throw new Exception('数据提交地址不能为空');
		
		$this->_apiSubmitUrl = $url;
		
		//初始化日志记录
		$this->_kLog = new KLogger(YOPENAPI_LOG_PATH, KLogger::INFO);
		$this->_kLog->setDateFormat('Y-m-d G:i:s.u P');//年月日时分秒毫秒 时区
	}

	/**
	 * @name 设置 CURL 句柄，并使用 JSON 格式传递数据
	 * @author WanJun
	 * @param string $actionUrl 链接地址
	 * @param array $postData 需要传送的数据
	 * @return string 链接错误信息或句柄返回数据
	 * @link http://php.net/manual/zh/function.curl-setopt.php
	 */
	public function setCurlHandle($postInfo = array()){
		
		$submitData = http_build_query($postInfo);				//这种方式，php 的POST对象可获取
		
		//记录日志
		YOPENAPI_LOG_IS_TRUE && $this->log('Curl-post', 'Service：'.$postInfo['service'], $submitData);
		
		//开始链接
		$ch = curl_init();
		curl_setopt($ch, CURLOPT_URL, $this->_apiSubmitUrl);
// 		curl_setopt($ch, CURLOPT_PORT, $this->_sslPort);  				//设置SSL端口号
		curl_setopt($ch, CURLOPT_HEADER, false);						//设置是否显示header $this->_showHeader
		curl_setopt($ch, CURLOPT_REFERER, $_SERVER['HTTP_REFERER']);  	//设置页面来源地址
		curl_setopt($ch, CURLOPT_TIMEOUT, 60);  						//设置超时时间
		curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false); 				// 信任任何证书
		curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 2); 					// 检查证书中设置域名是否与当前域名一致 ,当CURL版本大于 7.28.1时，不再使用1，改用2
		curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);  				//直接输出返回数据
		curl_setopt($ch, CURLOPT_BINARYTRANSFER, true) ;				//二进制传送
		curl_setopt($ch, CURLOPT_HTTP_VERSION, CURL_HTTP_VERSION_1_0);	//强制协议为1.0 2、CURL_HTTP_VERSION_1_1
		curl_setopt($ch, CURLOPT_POST, true);  							//使用post传递数据
	
		$headers = array(
				"Expect: 100-continue",
				"Content-type: application/x-www-form-urlencoded;charset=utf-8",
				"Cache-Control: no-cache",
				"Accept-Encoding: gzip",
				"Connection: keep-alive",
				"Pragma: no-cache"
		);
	
		curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
		curl_setopt($ch, CURLOPT_POSTFIELDS, $submitData);
	
		$execResult	= curl_exec($ch);									//执行
		$error		= curl_errno($ch);									//执行失败的错误号
		$info		= curl_getinfo($ch);
		curl_close($ch);
		
		if($error){
			$emsg = curl_error($ch);
			$last = error_get_last();
			
			$errorName = '与 平台OpenApi 建立链接句柄失败';
			$this->_kLog->logError($errorName);
			$this->_kLog->logError("请求地址：".$this->_apiSubmitUrl);
			$this->_kLog->logError("传送数据：".$submitData);
			$this->_kLog->logError("来源地址：".$_SERVER['HTTP_REFERER']);
			$this->_kLog->logError("Curl错误号：".$error);
			$this->_kLog->logError("错误信息：".($emsg ? $emsg : $last['message']));
			$this->_kLog->logInfo('----------------------------- Log End -----------------------------');
	
			if(OPEN_DEBUG) throw new Exception($errorName);
			exit;
		}
		
		$return = json_decode($execResult, true);
		$this->log('Curl-post', 'Service：'.$postInfo['service'], urldecode(http_build_query($return)));
		
		return $return;
	}
	
	/**
	 * @name 创建HTML POST 表单
	 * @author wanjun
	 * @param
	 * 	$datas array 表单元素
	 * @return void
	 */
	public function createHtml($postInfo = array()){
		
		//记录日志
		YOPENAPI_LOG_IS_TRUE && $this->log('Html-post', 'Service：'.$postInfo['service'], $this->_buildPairs($postInfo));
		
		$element = '';
		foreach($postInfo as $key=>$value){
			$element .= '<input type="hidden" name="'.$key.'" value=\''.$value.'\' />';
		}
	
		$html = '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">';
		$html .= '<html xmlns="http://www.w3.org/1999/xhtml">';
		$html .= '<head>';
		$html .= '<style>';
		$html .= 'body{text-align:center;}.logo-loading{width:200px;height:120px;margin:150px auto 0;border:1px transparent solid;background:url("") no-repeat scroll center center transparent;}.logo-loading img{display:block;margin:48px auto 0;margin-top:0px;width:200px;}';
		$html .= 'p{color:#04A057;font-size:20px;}';
		$html .= '</style>';
		$html .= '</head>';
		$html .= '<body onload="javascript:document.getElementById(\'openapi\').submit();">';
		$html .= '<div class="logo-loading">';
		$html .= '<img src="data:image/gif;base64," />';
		$html .= '</div>';
		$html .= '<p >系统执行中，请稍等...</p>';
		$html .= '<form name="openapi" id="openapi" enctype="multipart/form-data" method="post" action="'.$this->_apiSubmitUrl.'">';
		$html .= $element;
		$html .= '</form>';
		$html .= '</body>';
		$html .= '</html>';
	
		echo $html;
	}
	
	/**
	 * @name 记录日志
	 * @author WanJun
	 * @param string $type 日志记录类型，默认：curl；Curl执行信息，error：错误类型日志
	 * @param string $errorName 日志记录名称
	 * @param string $message 需要记录的信息
	 * @return void
	 * */
	public function log($type = 'curl', $errorName = '', $message = ''){
		
		$this->_kLog->logInfo('日志名称：'.$errorName);
		$this->_kLog->logInfo('提交类型：'.$type);
		$this->_kLog->logInfo('提交数据：'.$message);
		$this->_kLog->logInfo('----------------------------- Log End -----------------------------');
	}
	
	/**
	 * @name 将参数组装成post字符串
	 * @author wanjun
	 * @param
	 * 	$param array 待转换的数据
	 * @return string
	 */
	private function _buildPairs(array $param){
		$post = array();
		foreach ($param as $key => $value) {
			$post[] = $key . '=' . mb_convert_encoding($value, "utf-8", "auto");
		}
	
		return implode("&", $post);
	}
	
}//END CurlExecute


?>