    <script type="text/javascript">
        $(function(){

            $("body").on('click','.blockOverlay',function(){
                $.unblockUI();
            });

        });

        function do_check_client(flag) {
            var sale = $('#sale').val();
            if(flag == 'T')
            {
            	if($('#BusinessName').val() == '')
            	{
            		$.blockUI({ message: "<p>请输入公司名称！</p>" });
            		return;
            	}
            	else if($('#BusinessCard').val() == '')
            	{
            		$.blockUI({ message: "<p>请输入营业执照号码！</p>" });
            		return;
            	}
            }else if(($("#remark").val()).length == 0){
            	$.blockUI({ message: "<p>请输入未通过审核的原因</p>" });
        		return;
            }
            
            if(confirm('确认审核' + (flag == 'T' ? '通过' : '不通过'))) {
                $.post("do_manager.php",{
                    m:'client_check',
                    flag:flag,
                    id:"<?php echo $in['ID']; ?>",
                    uid:"<?php echo $in['UID']; ?>",
                    area:$('#data_CompanyArea').val(),
                    industry:$('#data_CompanyIndustry').val(),
                    name:$('#BusinessName').val(),
                    card:$('#BusinessCard').val(),
                    sale:sale,
                    remark:$("#remark").val()
                },function(data) {
                    if(data == 'ok') {
                        $.blockUI({
                            message : '<p>操作成功!</p>'
                        });
                        setTimeout(function(){
                            window.location.href = "client_verify.php?id=<?php echo $in['ID']; ?>";
                        },710);
                    } else {
                        $.blockUI({
                            message : '<p>'+data+'</p>'
                        });
                    }
                },'text');
            }
        }
    </script>
    </body>
